import jxl.* import groovy.util.*
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context) def projectDir = groovyUtils.projectPath File file = new File(projectDir) def firstLevelDir = file.getParent() log.info (firstLevelDir)
def TestCase = context.testCase def FilePath = firstLevelDir + "\\SoapUI\\DataProvider.xls" //def FilePath = "C:\\Users\\Huawei\\Desktop\\SoapUI\\DataProvider.xls" def count
Workbook WorkBook1 = Workbook.getWorkbook(new File(FilePath)) Sheet Sheet1 = WorkBook1.getSheet(0) PropertiesTestStep = TestCase.getTestStepByName("Properties") count = PropertiesTestStep.getPropertyValue("Counter").toInteger()
if (PropertiesTestStep.getPropertyValue("Total").toString() == "") PropertiesTestStep.setPropertyValue("Total", Sheet1.getRows().toString())
count++
Cell Field1 = Sheet1.getCell(0, count) Cell Field2 = Sheet1.getCell(1, count) log.info("Count is " + count.toString() + " Number: " + Field1.getContents() + " Word: " + Field2.getContents()) WorkBook1.close()
PropertiesTestStep.setPropertyValue("Name", Field1.getContents()) PropertiesTestStep.setPropertyValue("Value", Field2.getContents()) PropertiesTestStep.setPropertyValue("Counter", count.toString())
if (count == PropertiesTestStep.getPropertyValue("Total").toInteger()-1) PropertiesTestStep.setPropertyValue("End", "True")
